mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT A .USER_ID as userId,SUBSTR(d.jzrq, 1, 6) as month,dept.name as deptName,p.product_name as productName,e.display_name as displayName,sum(D .df) as total,count(D .df) as countSx,sum(yehz.JKJE) as jkje FROM BASIC_CUSTOMER_INFORMATION A, CUSTOMER_APPLICATION_INFO B, TY_REPAY_LSZ D ,SYS_DEPT_USER deptuser,SYS_DEPARTMENT dept,SYS_USER e ,product_attribute p,ty_repay_yehz yehz WHERE A .USER_ID=deptuser.USER_ID and deptuser.DEPT_ID=dept.id and A .USER_ID=e.id and A.ID=B.CUSTOMER_ID and B.JJH=D.JJH and p.id=b.product_id and D.JJH=yehz.JJH
and D.QXRX=#{fdate}
and p.product_name like '%'||#{product}||'%'
GROUP BY SUBSTR(d.jzrq, 1, 6),A .USER_ID,e.display_name,dept.name,p.product_name,yehz.JKJE ORDER BY A .USER_ID,SUBSTR(d.jzrq, 1, 6),dept.name
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT A .USER_ID as userId,SUBSTR(d.jzrq, 1, 6) as month,dept.name as deptName,p.product_name as productName,e.display_name as displayName,sum(D .df) as total,count(D .df) as countSx ,yehz.JKJE as jkje FROM BASIC_CUSTOMER_INFORMATION A, CUSTOMER_APPLICATION_INFO B, TY_REPAY_LSZ D ,SYS_DEPT_USER deptuser,SYS_DEPARTMENT dept,SYS_USER e , product_attribute p,ty_repay_yehz yehz WHERE A .USER_ID=deptuser.USER_ID and deptuser.DEPT_ID=dept.id and A .USER_ID=e.id and A.ID=B.CUSTOMER_ID and B.JJH=D.JJH and p.id = b.product_id and D.JJH=yehz.JJH
and D.QXRX=#{fdate}
and p.product_name like '%'||#{product}||'%'
and dept.name=#{deptName}
and e.display_name=#{displayName}
GROUP BY SUBSTR(d.jzrq, 1, 6),A .USER_ID,e.display_name,dept.name,p.product_name,yehz.JKJE ORDER BY A .USER_ID,SUBSTR(d.jzrq, 1, 6),dept.name
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select count(*) from (SELECT A .USER_ID as userId,SUBSTR(d.jzrq, 1, 6) as month,dept.name as deptName,p.product_name as productName,e.display_name as displayName,sum(D .df) as total,yehz.JKJE as jkje FROM BASIC_CUSTOMER_INFORMATION A, CUSTOMER_APPLICATION_INFO B, TY_REPAY_LSZ D ,SYS_DEPT_USER deptuser,SYS_DEPARTMENT dept,SYS_USER e , product_attribute p,ty_repay_yehz yehz WHERE A .USER_ID=deptuser.USER_ID and deptuser.DEPT_ID=dept.id and A .USER_ID=e.id and A.ID=B.CUSTOMER_ID and B.JJH=D.JJH and p.id = b.product_id and D.JJH=yehz.JJH
and D.QXRX=#{fdate}
and p.product_name like '%'||#{product}||'%'
and dept.name=#{deptName}
and e.display_name=#{displayName}
GROUP BY SUBSTR(d.jzrq, 1, 6),A .USER_ID,e.display_name,dept.name,p.product_name,yehz.JKJE ORDER BY A .USER_ID,SUBSTR(d.jzrq, 1, 6),dept.name)
