mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT risk.*, customer.chinese_name as chinese_name, customer.card_id as card_id, sysuser.display_name as display_name, risk.reason as reason FROM basic_customer_information customer INNER JOIN CUSTOMERBLACKLIST risk ON customer.id = risk.customerid LEFT JOIN sys_user sysuser ON sysuser.id = risk.userid WHERE 1=1
AND customer.chinese_name like '%${customerName}%'
AND customer.card_id like '%${cardId}%'
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM (SELECT customer.id as customer_id, customer.chinese_name as chinese_name, customer.card_id as card_id, sysuser.display_name as display_name, risk.reason as reason FROM basic_customer_information customer INNER JOIN CUSTOMERBLACKLIST risk ON customer.id = risk.customerid LEFT JOIN sys_user sysuser ON sysuser.id = risk.userid
where risk.userid=#{userid}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

mapper.delete:

			delete 
			FROM  CUSTOMERBLACKLIST 
			where userid=#{userid}
			and   customerid=#{customerid}

mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT customer.id as customer_id, customer.chinese_name as chinese_name, customer.card_id as card_id, customer.card_type as card_type FROM basic_customer_information customer LEFT JOIN CUSTOMERBLACKLIST risk ON customer.id = risk.customerid where customer.user_id=#{userid}
and customer.id not in (select customerid from CUSTOMERBLACKLIST)
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}
