mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM (
select a.deptname as Name, a.productName,sum(a.ffje) as jkje ,count(a.ywbh) as countSx,sum(a.df) as total from (select distinct tkmx.ywbh,base.khmc,protype.product_name as productName,sysuser.display_name,DeptName.name as deptname,tkmx.jkrq,shlx.df ,tkmx.ffje from ( select sum(lsz.df) as df ,tkmx.ywbh as ywbh from ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_yehz yehz, ty_repay_lsz lsz where base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and lsz.jjh=tkmx.jjh and lsz.zy='批量自动扣利息' group by tkmx.ywbh ) shlx, ( select distinct t.name,sysuser.id from sys_department t,sys_dept_user tt,sys_user sysuser where t.id=tt.dept_id and sysuser.id=tt.user_id) DeptName, ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_yehz yehz, ty_kdk_jh jh, ty_repay_lsz lsz WHERE base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and jh.ywbh=tkmx.ywbh||'HT' and lsz.jjh=tkmx.jjh and shlx.ywbh=tkmx.ywbh and DeptName.id=sysuser.id order by tkmx.jkrq ) a where 1=1
and a.JKRQ=#{fdate}
and productName like '%'||#{product}||'%'
group by a.deptname, a.productName order by nlssort (a.deptname ,'nls_sort=schinese_pinyin_m')
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

with jgnsxlx as ( SELECT * FROM ( select distinct tkmx.ywbh,base.khmc,protype.product_name as productName,sysuser.display_name,tkmx.jkrq,shlx.df ,tkmx.ffje from ( select sum(lsz.df) as df ,tkmx.ywbh as ywbh from ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_yehz yehz, ty_repay_lsz lsz where base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and lsz.jjh=tkmx.jjh and lsz.zy='批量自动扣利息' group by tkmx.ywbh ) shlx, ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_yehz yehz, ty_kdk_jh jh, ty_repay_lsz lsz WHERE base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and substr(jh.ywbh,1,length(jh.ywbh)-2)=tkmx.ywbh and lsz.jjh=tkmx.jjh and shlx.ywbh=tkmx.ywbh order by tkmx.jkrq ))
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select DeptName.Name, protype.product_name as productName,sysuser.display_name as displayName,sum(jgnsxlx.df) as total,count(jgnsxlx.df) as countSx,sum(jgnsxlx.ffje) as jkje from jgnsxlx, ( select distinct t.name,sysuser.id from sys_department t,sys_dept_user tt,sys_user sysuser where t.id=tt.dept_id and sysuser.id=tt.user_id) DeptName, ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_yehz yehz WHERE base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and jgnsxlx.ywbh=tkmx.ywbh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and DeptName.id=sysuser.id
and tkmx.ffrq=#{fdate}
and protype.product_name like '%'||#{product}||'%'
and DeptName.name=#{deptName}
and sysuser.display_name like '%'||#{displayName}||'%'
group by DeptName.Name,sysuser.display_name, protype.product_name order by nlssort (protype.product_name ,'nls_sort=schinese_pinyin_m')
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

with jgnsxlx as ( SELECT * FROM ( select distinct tkmx.ywbh,base.khmc,protype.product_name as productName,sysuser.display_name,tkmx.jkrq,shlx.df ,tkmx.ffje from ( select sum(lsz.df) as df ,tkmx.ywbh as ywbh from ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_yehz yehz, ty_repay_lsz lsz where base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and lsz.jjh=tkmx.jjh and lsz.zy='批量自动扣利息' group by tkmx.ywbh ) shlx, ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_yehz yehz, ty_kdk_jh jh, ty_repay_lsz lsz WHERE base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and substr(jh.ywbh,1,length(jh.ywbh)-2)=tkmx.ywbh and lsz.jjh=tkmx.jjh and shlx.ywbh=tkmx.ywbh order by tkmx.jkrq )) select count(*) from ( select DeptName.Name, protype.product_name as productName,sysuser.display_name as displayName,sum(jgnsxlx.df) as total,count(jgnsxlx.df) as countSx,sum(jgnsxlx.ffje) as jkje from jgnsxlx, ( select distinct t.name,sysuser.id from sys_department t,sys_dept_user tt,sys_user sysuser where t.id=tt.dept_id and sysuser.id=tt.user_id) DeptName, ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_yehz yehz WHERE base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and jgnsxlx.ywbh=tkmx.ywbh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and DeptName.id=sysuser.id
and tkmx.ffrq=#{fdate}
and protype.product_name like '%'||#{product}||'%'
and DeptName.name =#{deptName}
and sysuser.display_name like '%'||#{displayName}||'%'
group by DeptName.Name,sysuser.display_name, protype.product_name order by nlssort (protype.product_name ,'nls_sort=schinese_pinyin_m') ) a
