mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select users1.DISPLAY_NAME displayname, T_JX_PARAMETERS.MONTH_LOAN_NUM monthloannum, T_JX_PARAMETERS.MONTH_EFFECT_NUM montheffectnum, sum(ffje1.FFJE) byffje, sum(ffje2.FFJE) ffje, sum(TY_REPAY_LSZ.df) zsx from (select t.FFJE ,base.id as id from TY_REPAY_TKMX t,ty_customer_base base ,basic_customer_information b where base.khnm = t.KHH and b.TY_CUSTOMER_ID = base.ID and t.DQRQ>(select to_char(trunc(sysdate,'MONTH'),'yyyy-mm-dd') First_DayOfMonth from dual) ) ffje1, (select t.FFJE ,base.id as id from TY_REPAY_TKMX t,ty_customer_base base ,basic_customer_information b where base.khnm = t.KHH and b.TY_CUSTOMER_ID = base.ID ) ffje2, SYS_USER users1, T_JX_PARAMETERS, TY_REPAY_LSZ, ty_repay_tkmx, BASIC_CUSTOMER_INFORMATION b, CUSTOMER_APPLICATION_INFO info, ty_customer_base base where users1.id=T_JX_PARAMETERS.customer_manager_id and users1.id=b.USER_ID and b.TY_CUSTOMER_ID = base.ID and base.khnm = ty_repay_tkmx.KHH and info.customer_id = b.id and ffje1.id=base.id and ffje2.id=base.id and ty_repay_tkmx.JJH=TY_REPAY_LSZ.JJH
and users1.ID=#{customerManagerId}
group by users1.DISPLAY_NAME, T_JX_PARAMETERS.MONTH_LOAN_NUM, T_JX_PARAMETERS.MONTH_EFFECT_NUM
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select count(*) from SYS_USER users1, T_JX_PARAMETERS, TY_REPAY_LSZ, ty_repay_tkmx, BASIC_CUSTOMER_INFORMATION b, CUSTOMER_APPLICATION_INFO info, ty_customer_base base where users1.id=T_JX_PARAMETERS.customer_manager_id and users1.id=b.USER_ID and b.TY_CUSTOMER_ID = base.ID and base.KHH = ty_repay_tkmx.KHH and info.customer_id = b.id and
ty_repay_tkmx.JJH=TY_REPAY_LSZ.JJH
and users1.ID=#{customerManagerId}
