mapper.select:
		SELECT *  FROM manager_performmance 
		WHERE manager_id=#{managerId}
		and trunc(CRATEDAY)=trunc(sysdate)

SELECT sum (p.VISITCOUNT) AS VISITCOUNT_S,sum (p.APPLYCOUNT) AS APPLYCOUNT_S, sum (p.APPLYREFUSE) AS APPLYREFUSE_S, sum (p.CREDITCOUNT) AS CREDITCOUNT_S, sum (p.CREDITREFUSE) AS CREDITREFUSE_S, sum (p.REALYCOUNT) AS REALYCOUNT_S, sum (p.REPORTCOUNT) AS REPORTCOUNT_S, sum (p.INTERNALCOUNT) AS INTERNALCOUNT_S, sum (p.MEETINGCOUT) AS MEETINGCOUT_S,sum (p.PASSCOUNT) AS PASSCOUNT_S, sum (p.SIGNCOUNT) AS SIGNCOUNT_S, sum (p.GIVEMONEYCOUNT) AS GIVEMONEYCOUNT_S FROM MANAGER_PERFORMMANCE p where p.manager_id = #{managerId}
and p.crateday >= to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')>= p.crateday
