mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT salary.*, usr.display_name as managerName,c.short_name as shortName FROM manager_salary salary , sys_user usr,SYS_DEPT_USER b,SYS_DEPARTMENT c WHERE 1=1 and usr.id = salary.customer_id and salary.CUSTOMER_ID=b.USER_ID and b.DEPT_ID=c.id
AND usr.display_name like '%${managerName}%'
AND salary.month=#{month}
AND salary.year=#{year}
order by salary.year desc,salary.month desc,c.name
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT count(*) FROM manager_salary salary INNER join sys_user usr ON usr.id = salary.customer_id WHERE 1=1
AND usr.display_name like '%${managerName}%'
AND salary.month=#{month}
AND salary.year=#{year}

