SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select distinct tkmx.ywbh as ywbh, base.khmc as cname, base.zjhm as zjhm, tkmx.jkrq, tkmx.ffje as ffje, yehz.DKYE as balamt, yehz.bnqx+yehz.bwqx as debtinterest, tkmx.ffje as reqlmt, jh.ts as delayamtdays, jh.ts as delayinterestdays from (select tkmx.ywbh as ywbh, max(to_number(jh.qs)) as qs from TY_REPAY_TKMX tkmx, TY_CUSTOMER_BASE b, TY_REPAY_YEHZ yehz, ty_kdk_jh jh, ty_customer_rygl rygl, sys_user sysuser where 1=1 and b.khnm=tkmx.khh and yehz.JJH = tkmx.JJH and jh.ywbh=tkmx.ywbh||'HT' and b.khjl=rygl.dm and rygl.ddrq=sysuser.external_id group by tkmx.ywbh) maxqs, ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_yehz yehz, ty_kdk_jh jh, ty_repay_lsz lsz WHERE base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and jh.ywbh=tkmx.ywbh||'HT' and lsz.jjh=tkmx.jjh and maxqs.ywbh=tkmx.ywbh and maxqs.qs=jh.qs
AND sysuser.id=#{userId}
AND trim(tkmx.ywbh)=#{busiCode}
AND trim(base.zjhm)=#{cardNum}
order by tkmx.jkrq
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select distinct tkmx.ywbh as ywbh, base.khh as khh, base.khmc as cname, base.Khlx as khlx, base.Zjlx as zjlx, base.zjhm as zjhm, tkmx.jkrq, tkmx.ffje as ffje, yehz.DKYE as balamt, tkmx.jkrq as jkrq, tkmx.dqrq as dqrq, yehz.bnqx+yehz.bwqx as debtinterest, tkmx.ffje as reqlmt, jh.ts as delayamtdays, jh.ts as delayinterestdays from (select tkmx.ywbh as ywbh, max(to_number(jh.qs)) as qs from TY_REPAY_TKMX tkmx, TY_CUSTOMER_BASE b, TY_REPAY_YEHZ yehz, ty_kdk_jh jh, ty_customer_rygl rygl, sys_user sysuser where 1=1 and b.khnm=tkmx.khh and yehz.JJH = tkmx.JJH and jh.ywbh=tkmx.ywbh||'HT' and b.khjl=rygl.dm and rygl.ddrq=sysuser.external_id group by tkmx.ywbh) maxqs,
ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_yehz yehz, ty_kdk_jh jh, ty_repay_lsz lsz WHERE base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and jh.ywbh=tkmx.ywbh||'HT' and lsz.jjh=tkmx.jjh and maxqs.ywbh=tkmx.ywbh and maxqs.qs=jh.qs
AND sysuser.id=#{userId}
AND trim(tkmx.ywbh)=#{busiCode}
AND trim(base.zjhm)=#{cardNum}
order by tkmx.jkrq

select count(distinct tkmx.ywbh) from (select tkmx.ywbh as ywbh, max(to_number(jh.qs)) as qs from TY_REPAY_TKMX tkmx, TY_CUSTOMER_BASE b, TY_REPAY_YEHZ yehz, ty_kdk_jh jh, ty_customer_rygl rygl, sys_user sysuser where 1=1 and b.khnm=tkmx.khh and yehz.JJH = tkmx.JJH and jh.ywbh=tkmx.ywbh||'HT' and b.khjl=rygl.dm and rygl.ddrq=sysuser.external_id group by tkmx.ywbh) maxqs, ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_yehz yehz, ty_kdk_jh jh, ty_repay_lsz lsz WHERE base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and jh.ywbh=tkmx.ywbh||'HT' and lsz.jjh=tkmx.jjh and maxqs.ywbh=tkmx.ywbh and maxqs.qs=jh.qs
AND sysuser.id=#{userId}
AND trim(tkmx.ywbh)=#{busiCode}
AND trim(base.zjhm)=#{cardNum}
order by tkmx.jkrq

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select distinct tkmx.ywbh, b.khmc, tkmx.jkrq, shbj.df as bj, shlx.df as lx, tkmx.jxfs, shbj.jzrq, yehz.create_time from (select sum(lsz.df) as df , tkmx.ywbh , c.zjhm from ty_customer_rygl rygl, TY_REPAY_TKMX tkmx , TY_REPAY_YEHZ yehz, ty_repay_lsz lsz, ty_customer_base c, ty_product_type protype, sys_user sysuser where c.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and c.KHNM = tkmx.khh and yehz.JJH = tkmx.JJH and protype.product_code=tkmx.cpmc and lsz.jjh=tkmx.jjh and lsz.zy='批量自动扣利息'
group by tkmx.ywbh , c.zjhm) shlx, (select c.khmc, sum(lsz.df) as df , tkmx.ywbh , c.zjhm, max(lsz.jzrq) as jzrq from ty_customer_rygl rygl, TY_REPAY_TKMX tkmx , TY_REPAY_YEHZ yehz, ty_repay_lsz lsz, ty_customer_base c, ty_product_type protype, sys_user sysuser where c.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and c.KHNM = tkmx.khh and yehz.JJH = tkmx.JJH and protype.product_code=tkmx.cpmc and lsz.jjh=tkmx.jjh and (lsz.zy='批量自动扣本金' or lsz.zy='贷款柜台提前还款')
group by tkmx.ywbh , c.zjhm ,c.khmc) shbj, ty_repay_tkmx tkmx, ty_customer_base b, ty_customer_rygl rygl, sys_user sysuser, ty_kdk_jh jh, ty_repay_yehz yehz, ty_repay_lsz lsz where b.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and b.khnm=tkmx.khh and jh.ywbh=tkmx.ywbh||'HT' and yehz.jjh=tkmx.jjh and tkmx.jjh =lsz.jjh and
shlx.ywbh=tkmx.ywbh and shlx.zjhm=b.zjhm and shbj.ywbh=tkmx.ywbh and shbj.zjhm=b.Zjhm
AND trim(tkmx.ywbh)=#{busiCode}
order by tkmx.jkrq
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM (
select distinct tkmx.YWBH , b.id as id , b.KHH, b.KHMC, b.ZJHM, rygl.SSBM, b.SJ, b.SXED, b.GHJL, tkmx.JKRQ as LOANDATE, tkmx.DQRQ, tkmx.DKQX, tkmx.JZLL, tkmx.FFJE as REQLMT, (case when(to_date(max(lsz.jzrq),'yyyy-mm-dd')-to_date(tkmx.dqrq,'yyyy-mm-dd')) > 0 then (to_date(max(lsz.jzrq),'yyyy-mm-dd')-to_date(tkmx.dqrq,'yyyy-mm-dd')) else null end ) as DELAYINTERESTDAYS, (case when(to_date(max(lsz.jzrq),'yyyy-mm-dd')-to_date(tkmx.dqrq,'yyyy-mm-dd')) > 0 then (to_date(max(lsz.jzrq),'yyyy-mm-dd')-to_date(tkmx.dqrq,'yyyy-mm-dd')) else null end )as DELAYAMTDAYS, max(lsz.jzrq) as DLAYDT, yehz.SHBJ, yehz.JKJE, yehz.KSQXRQ, yehz.SHLX as PAYDEBT, yehz.DKYE as YE , yehz.BNQX, yehz.BWQX, yehz.DKYE as BALAMT , yehz.dkye as yxye, sysuser.id userid, sysuser.display_name from ty_repay_tkmx tkmx, ty_customer_base b, ty_customer_rygl rygl, sys_user sysuser, ty_kdk_jh jh, ty_repay_yehz yehz, ty_repay_lsz lsz where b.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and b.khnm=tkmx.khh and jh.ywbh=tkmx.ywbh||'HT' and yehz.jjh=tkmx.jjh and tkmx.jjh =lsz.jjh
and sysuser.id =#{userId}
AND trim(tkmx.ywbh)=#{busiCode}
AND b.khmc=#{customerName}
group by tkmx.YWBH ,b.id ,b.KHH,b.KHMC, b.ZJHM,rygl.SSBM, b.SJ, b.SXED, b.GHJL,tkmx.JKRQ , tkmx.DQRQ, tkmx.DKQX,tkmx.JZLL, tkmx.FFJE , yehz.SHBJ,yehz.JKJE, yehz.KSQXRQ,yehz.SHLX, yehz.DKYE,yehz.BNQX, yehz.BWQX,yehz.DKYE , yehz.dkye , sysuser.display_name, sysuser.id order by tkmx.jkrq
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select count(*) from ( select distinct tkmx.YWBH , b.id as id , b.KHH, b.KHMC, b.ZJHM, rygl.SSBM, b.SJ, b.SXED, b.GHJL, tkmx.JKRQ as LOANDATE, tkmx.DQRQ, tkmx.DKQX, tkmx.JZLL, tkmx.FFJE as REQLMT, (case when(to_date(max(lsz.jzrq),'yyyy-mm-dd')-to_date(tkmx.dqrq,'yyyy-mm-dd')) > 0 then (to_date(max(lsz.jzrq),'yyyy-mm-dd')-to_date(tkmx.dqrq,'yyyy-mm-dd')) else null end ) as DELAYINTERESTDAYS, (case when(to_date(max(lsz.jzrq),'yyyy-mm-dd')-to_date(tkmx.dqrq,'yyyy-mm-dd')) > 0 then (to_date(max(lsz.jzrq),'yyyy-mm-dd')-to_date(tkmx.dqrq,'yyyy-mm-dd')) else null end )as DELAYAMTDAYS, max(lsz.jzrq) as DLAYDT, yehz.SHBJ, yehz.JKJE, yehz.KSQXRQ, yehz.SHLX as PAYDEBT, yehz.DKYE, yehz.BNQX, yehz.BWQX, yehz.DKYE as BALAMT , yehz.dkye as yxye, sysuser.id userid, sysuser.display_name from ty_repay_tkmx tkmx, ty_customer_base b, ty_customer_rygl rygl, sys_user sysuser, ty_kdk_jh jh, ty_repay_yehz yehz, ty_repay_lsz lsz where b.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and b.khnm=tkmx.khh and jh.ywbh=tkmx.ywbh||'HT' and yehz.jjh=tkmx.jjh and tkmx.jjh =lsz.jjh
and sysuser.id =#{userId}
AND trim(tkmx.ywbh)=#{busiCode}
AND b.khmc=#{customerName}
group by tkmx.YWBH ,b.id ,b.KHH,b.KHMC, b.ZJHM,rygl.SSBM, b.SJ, b.SXED, b.GHJL,tkmx.JKRQ , tkmx.DQRQ, tkmx.DKQX,tkmx.JZLL, tkmx.FFJE , yehz.SHBJ,yehz.JKJE, yehz.KSQXRQ,yehz.SHLX, yehz.DKYE,yehz.BNQX, yehz.BWQX,yehz.DKYE , yehz.dkye , sysuser.display_name, sysuser.id order by tkmx.jkrq ) a

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select tkmx.ywbh,basic.CHINESE_NAME,basic.CARD_ID,b.SJ as TELEPHONE,b.Cshy as industry,b.GTSPMC as spmc,b.YYDZ as RESIDENTIAL_ADDRESS, yehz.JKJE,tkmx.JKRQ,pro.AUDIT_TIME,pro.REFUSAL_REASON,sysuser.DISPLAY_NAME, pro.PROCESS_OP_STATUS from basic_customer_information basic, ty_customer_base b, ty_repay_tkmx tkmx, ty_repay_yehz yehz, customer_application_info app, customer_application_process pro, sys_user sysuser where b.id=basic.ty_customer_id and b.khnm=tkmx.khh and tkmx.jjh=yehz.jjh and app.customer_id=basic.id and app.id=pro.application_id and sysuser.id=basic.user_id and pro.PROCESS_OP_STATUS ='REJECTAPPROVE'
AND trim(tkmx.ywbh)=#{busiCode}
AND basic.CHINESE_NAME =#{customerName}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select count(*) from basic_customer_information basic, ty_customer_base b, ty_repay_tkmx tkmx, ty_repay_yehz yehz, customer_application_info app, customer_application_process pro, sys_user sysuser where b.id=basic.ty_customer_id and b.khnm=tkmx.khh and tkmx.jjh=yehz.jjh and app.customer_id=basic.id and app.id=pro.application_id and sysuser.id=basic.user_id and pro.PROCESS_OP_STATUS ='REJECTAPPROVE'
AND trim(tkmx.ywbh)=#{busiCode}
AND basic.CHINESE_NAME =#{customerName}

