and sysuser.id =#{userId}
AND trim(tkmx.ywbh)=#{busiCode}
AND b.khmc=#{customerName}
group by tkmx.YWBH ,b.id ,b.KHH,b.KHMC, b.ZJHM,rygl.SSBM, b.SJ, b.SXED, b.GHJL,tkmx.JKRQ , tkmx.DQRQ, tkmx.DKQX,tkmx.JZLL, tkmx.FFJE , yehz.SHBJ,yehz.JKJE, yehz.KSQXRQ,yehz.SHLX, yehz.DKYE,yehz.BNQX, yehz.BWQX,yehz.DKYE , yehz.dkye , sysuser.display_name, sysuser.id order by tkmx.jkrq

SELECT *from (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select bai.spmc, bai.chinese_name, (select t.name from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = su.id)) as teamname, (SELECT A.TYPE_NAME FROM DICT A ,PRODUCT_ATTRIBUTE p WHERE A .DICT_TYPE = 'CMMSCLNM-贷款担保方式' and A.TYPE_CODE=p.assure_means and p.id=cai.product_id) as assure_means, su.display_name, cai.created_time, cai.apply_quota, cai.status, cai.id
from customer_application_info cai left join customer_application_process aa on cai.id=aa.application_id left join basic_customer_information bai on cai.customer_id=bai.id left join sys_user su on su.id=bai.user_id left join ty_customer_base base on bai.ty_customer_id=base.id left join PRODUCT_ATTRIBUTE p on p.id=cai.product_id left join wf_process_record pr on pr.id = aa.serial_number left join wf_status_queue_record t on t.current_process = pr.id left join wf_status_info s on t.current_status = s.id where 1=1
and bai.chinese_name like '%'||#{customername}||'%'
order by cai.created_time desc
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}
