mapper.select:
	SELECT * FROM
	(SELECT ROW_.*,ROWNUM ROWNUM_
	FROM (
	select move.*,base.CHINESE_NAME,base.CARD_ID
	from
	TY_CUSTOMER_MOVE move,
	BASIC_CUSTOMER_INFORMATION base
	WHERE 1=1 and base.ID = move.CUSTOMER_ID and move.status ='2'
	
		AND base.CARD_ID=#{certiCode}
	

	
		AND base.CHINESE_NAME=#{customerName}
	
		 		
							) ROW_
						WHERE ROWNUM <= #{limit}*#{page} + #{limit}) 
					WHERE ROWNUM_ > #{limit}*#{page}

 select count(*) from TY_CUSTOMER_MOVE move,
			 	      BASIC_CUSTOMER_INFORMATION base
 WHERE 1=1 and base.ID = move.CUSTOMER_ID and move.status ='2'

	AND base.CARD_ID=#{certiCode}

			 

    AND base.CHINESE_NAME=#{customerName}

 		select move.*,base.CHINESE_NAME,base.CARD_ID,ROWNUM row_index from TY_CUSTOMER_MOVE move,
					 			   BASIC_CUSTOMER_INFORMATION base 
					  WHERE 1=1 and base.ID = move.CUSTOMER_ID and move.status ='2'
					
						AND base.CARD_ID=#{certiCode}
					
					 
					
						AND base.CHINESE_NAME=#{customerName}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM (
select distinct tkmx.ywbh,base.khmc as CHINESE_NAME ,base.ZJHM as CARD_ID, protype.product_name as CPMC,tkmx.FFJE,tkmx.HTLL ,yehz.QXRQ, sysuser.display_name as KHJL,DeptName.Name as BLJG,tkmx.jkrq from
( select distinct t.name,sysuser.id from sys_department t,sys_dept_user tt,sys_user sysuser where t.id=tt.dept_id and sysuser.id=tt.user_id) DeptName, ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, ty_repay_lsz lsz, SYS_USER sysuser , ty_kdk_jh jh, ty_repay_yehz yehz WHERE base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and lsz.jjh=tkmx.jjh and jh.ywbh=tkmx.ywbh||'HT' and DeptName.id=sysuser.id
and base.zjhm=#{certiCode}
and base.khmc=#{customerName}
AND DeptName.Name=#{organName}
AND sysuser.display_name=#{custManagerName}
AND app.ACCOUNT_STATUS=#{status}
order by tkmx.jkrq desc
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select distinct base.khmc as cname ,base.zjhm as cardtype,tkmx.FFJE as money,tkmx.HTLL as baserate,yehz.QXRQ as interest, sysuser.display_name as busimanager,DeptName.Name as name from
( select distinct t.name,sysuser.id from sys_department t,sys_dept_user tt,sys_user sysuser where t.id=tt.dept_id and sysuser.id=tt.user_id) DeptName, ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, ty_repay_lsz lsz, SYS_USER sysuser , ty_kdk_jh jh, ty_repay_yehz yehz WHERE base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and lsz.jjh=tkmx.jjh and jh.ywbh=tkmx.ywbh||'HT' and DeptName.id=sysuser.id
and base.zjhm=#{certiCode}
and base.khmc=#{customerName}
AND DeptName.Name=#{organName}
AND sysuser.display_name=#{custManagerName}
AND app.ACCOUNT_STATUS=#{status}

select count(distinct tkmx.ywbh) from
( select distinct t.name,sysuser.id from sys_department t,sys_dept_user tt,sys_user sysuser where t.id=tt.dept_id and sysuser.id=tt.user_id) DeptName, ty_customer_rygl rygl, ty_customer_base base, ty_product_type protype, ty_repay_tkmx tkmx, ty_repay_lsz lsz, SYS_USER sysuser , ty_kdk_jh jh, ty_repay_yehz yehz WHERE base.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and base.khnm=tkmx.khh and tkmx.JJH=yehz.JJH and protype.product_code=tkmx.cpmc and lsz.jjh=tkmx.jjh and jh.ywbh=tkmx.ywbh||'HT' and DeptName.id=sysuser.id
and base.zjhm=#{certiCode}
and base.khmc=#{customerName}
AND DeptName.Name=#{organName}
AND sysuser.display_name=#{custManagerName}
AND app.ACCOUNT_STATUS=#{status}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM (
select b.chinese_name as CHINESE_NAME, info.card_id as CARD_ID, prod.product_name as CPMC, to_char(app.created_time,'yyyy-MM-dd') as JKRQ, app.apply_quota as APPLY_QUOTA , to_char(pro.audit_time,'yyyy-MM-dd') as AUDIT_TIME, pro.refusal_reason as REFUSAL_REASON, sysuser.display_name as DISPLAY_NAME, (select t.name from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = sysuser.id))as BLJG from BASIC_CUSTOMER_INFORMATION b, customer_application_info app, basic_customer_information info, product_attribute prod, customer_application_process pro, sys_user sysuser where app.customer_id = b.id and app.customer_id = info.id and app.product_id = prod.id and app.id = pro.application_id and b.user_id=sysuser.id and app.status ='refuse'
AND b.CARD_ID=#{certiCode}
AND sysuser.DISPLAY_NAME=#{custManagerName}
AND a.name=#{organName}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select count(*) from BASIC_CUSTOMER_INFORMATION b, customer_application_info app, basic_customer_information info, product_attribute prod, customer_application_process pro, sys_user sysuser where app.customer_id = b.id and app.customer_id = info.id and app.product_id = prod.id and app.id = pro.application_id and b.user_id=sysuser.id and app.status ='refuse'
AND b.CARD_ID=#{certiCode}
AND sysuser.DISPLAY_NAME=#{custManagerName}
AND a.name=#{organName}

 select a.*,ROWNUM row_index from bjjdktjbb a WHERE 1=1 

	AND a.cardtype=#{certiCode}


    AND a.cname=#{customerName}


    AND a.name=#{organName}


	AND a.busimanager=#{custManagerName}

