group by tkmx.ywbh , c.zjhm ) shlx,
(select sum(lsz.df) as df , tkmx.ywbh , c.zjhm from ty_customer_rygl rygl, TY_REPAY_TKMX tkmx , TY_REPAY_YEHZ yehz, ty_repay_lsz lsz, ty_customer_base c, ty_product_type protype, sys_user sysuser where c.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and c.KHNM = tkmx.khh and yehz.JJH = tkmx.JJH and protype.product_code=tkmx.cpmc and lsz.jjh=tkmx.jjh
and (lsz.zy='批量自动扣本金' or lsz.zy='贷款柜台提前还款') group by tkmx.ywbh , c.zjhm) shbj, ty_customer_rygl rygl, TY_REPAY_TKMX tkmx , TY_REPAY_YEHZ yehz, ty_product_type protype, ty_customer_base c, TY_KDK_JH jh, sys_user sysuser where c.khjl=rygl.dm and rygl.ddrq=sysuser.external_id and c.KHNM = tkmx.khh and protype.product_code=tkmx.cpmc and yehz.JJH = tkmx.JJH and jh.ywbh=tkmx.ywbh||'HT' and maxqs.cardid=c.zjhm and maxqs.qs=jh.qs and shlx.ywbh=tkmx.ywbh and shlx.zjhm=c.zjhm and shbj.ywbh=tkmx.ywbh and shbj.zjhm=c.zjhm and tkmx.jqrq is not null and tkmx.jqrq <= tkmx.Dqrq
AND c.zjhm=#{certiCode}
