mapper.select:
	SELECT * FROM
	(SELECT ROW_.*,ROWNUM ROWNUM_
	FROM (
	select move.*,base.CHINESE_NAME,base.CARD_ID
	from
	TY_CUSTOMER_MOVE move,
	BASIC_CUSTOMER_INFORMATION base
	WHERE 1=1 and base.ID = move.CUSTOMER_ID and move.status ='2'
	
		AND base.CARD_ID=#{certiCode}
	

	
		AND base.CHINESE_NAME=#{customerName}
	
		 		
							) ROW_
						WHERE ROWNUM <= #{limit}*#{page} + #{limit}) 
					WHERE ROWNUM_ > #{limit}*#{page}

 select count(*) from TY_CUSTOMER_MOVE move,
			 	      BASIC_CUSTOMER_INFORMATION base
 WHERE 1=1 and base.ID = move.CUSTOMER_ID and move.status ='2'

	AND base.CARD_ID=#{certiCode}

			 

    AND base.CHINESE_NAME=#{customerName}

 		select move.*,base.CHINESE_NAME,base.CARD_ID,ROWNUM row_index from TY_CUSTOMER_MOVE move,
					 			   BASIC_CUSTOMER_INFORMATION base 
					  WHERE 1=1 and base.ID = move.CUSTOMER_ID and move.status ='2'
					
						AND base.CARD_ID=#{certiCode}
					
					 
					
						AND base.CHINESE_NAME=#{customerName}

					SELECT * FROM
			(SELECT ROW_.*,ROWNUM ROWNUM_
				FROM (
                select distinct tym.ywbh,tym.khmc as CHINESE_NAME ,
					tym.ZJHM as CARD_ID, pro.product_name as CPMC,tym.Reqlmt as FFJE,tkmx.sfbl as JZLL ,
					tym.Loandate as JKRQ, tym.display_name as KHJL,tym.ywjg as BLJG 
					from ty_mibusidata tym , ty_repay_tkmx tkmx, ty_product_type pro , sys_department t,sys_dept_user tt
					where 1=1 and 
					tym.ywbh=tkmx.ywbh and 
					tkmx.cpmc=pro.product_code AND 
					t.id=tt.dept_id and 
					tym.userid=tt.user_id
               
						 and tym.zjhm=#{certiCode}
					
								 
					
					    and tym.khmc=#{customerName}
					
					
					
					   and t.name=#{organName}
					
					
					
						AND tym.display_name=#{custManagerName}
					
					
					
						AND app.ACCOUNT_STATUS=#{status}
					 
					 order by tym.Loandate  desc
		 		
							) ROW_
						WHERE ROWNUM <= #{limit}*#{page} + #{limit}) 
					WHERE ROWNUM_ > #{limit}*#{page}

					
 		 			select distinct  tkmx.ywbh,tym.khmc as cname ,tym.ZJHM as cardtype,pro.product_name as prodName,tym.Reqlmt as money,tkmx.sfbl as baserate,tym.Loandate as interest, tym.display_name as busimanager,tym.ywjg  as name 
                       from   ty_mibusidata tym  ,
					     ty_repay_tkmx tkmx,
					     ty_product_type pro, sys_department t,sys_dept_user tt
						where 1=1 and 
						tym.ywbh=tkmx.ywbh and 
						tkmx.cpmc=pro.product_code AND 
						t.id=tt.dept_id and 
						tym.userid=tt.user_id
               
						 and tym.zjhm=#{certiCode}
					
								 
					
					    and tym.khmc=#{customerName}
					
					
					
					       and t.name=#{organName}
					
					
					
						AND tym.display_name=#{custManagerName}
					
					
					
						AND app.ACCOUNT_STATUS=#{status}
					 
					 order by tym.Loandate  desc

		  select  count(ywbh)
                 from
	                   
	                   ( 
					 select   distinct tym.ywbh,tym.khmc as CHINESE_NAME ,tym.ZJHM as CARD_ID, pro.product_name as CPMC,tym.Reqlmt as FFJE,tym.JZLL ,tym.Loandate as JKRQ,  tym.display_name as KHJL,tym.ywjg as BLJG
					     from ty_mibusidata tym  ,
					     ty_repay_tkmx tkmx,
					     ty_product_type pro, sys_department t,sys_dept_user tt
						where 1=1 and 
						tym.ywbh=tkmx.ywbh and 
						tkmx.cpmc=pro.product_code AND 
						t.id=tt.dept_id and 
						tym.userid=tt.user_id
               
						 and tym.zjhm=#{certiCode}
					
								 
					
					    and tym.khmc=#{customerName}
					
					
					
					    AND t.name=#{organName}
					
					
					
						AND tym.display_name=#{custManagerName}
					
					
					
						AND app.ACCOUNT_STATUS=#{status}
					 
					 order by tym.Loandate  desc)

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM (
select b.chinese_name as CHINESE_NAME, info.card_id as CARD_ID, prod.product_name as CPMC, to_char(app.created_time,'yyyy-MM-dd') as JKRQ, app.apply_quota as APPLY_QUOTA , to_char(app.Modified_Time,'yyyy-MM-dd') as AUDIT_TIME, pro.refusal_reason as REFUSAL_REASON, sysuser.display_name as DISPLAY_NAME, (select t.name from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = sysuser.id))as BLJG from BASIC_CUSTOMER_INFORMATION b, customer_application_info app, basic_customer_information info, product_attribute prod, customer_application_process pro, sys_user sysuser where app.customer_id = b.id and app.customer_id = info.id and app.product_id = prod.id and app.id = pro.application_id and b.user_id=sysuser.id and app.status ='refuse'
AND b.CARD_ID=#{certiCode}
AND sysuser.DISPLAY_NAME=#{custManagerName}
AND (select t.name from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = sysuser.id))=#{organName}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select count(*) from BASIC_CUSTOMER_INFORMATION b, customer_application_info app, basic_customer_information info, product_attribute prod, customer_application_process pro, sys_user sysuser where app.customer_id = b.id and app.customer_id = info.id and app.product_id = prod.id and app.id = pro.application_id and b.user_id=sysuser.id and app.status ='refuse'
AND b.CARD_ID=#{certiCode}
AND sysuser.DISPLAY_NAME=#{custManagerName}
AND (select t.name from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = sysuser.id))=#{organName}

 select a.*,ROWNUM row_index from bjjdktjbb a WHERE 1=1 

	AND a.cardtype=#{certiCode}


    AND a.cname=#{customerName}


    AND a.name=#{organName}


	AND a.busimanager=#{custManagerName}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select distinct tkmx.ywbh, c.khmc as CHINESE_NAME,c.zjhm as CARD_ID,protype.product_name as CPMC,yehz.qxrq as JZRQ,tkmx.DQRQ,tkmx.ffje as DF,tkmx.SFBL as JZLL,jh.QS,sysuser.display_name as KHJL, (select t.name from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = sysuser.id))as YWJG, shlx.df as YHLX,shbj.df as JE from ( select c.zjhm as cardid, max(to_number(jh.qs)) as qs, max(to_number(lsz.jzrq)) as maxjzrq from basic_customer_information basic, TY_REPAY_TKMX tkmx , TY_REPAY_YEHZ yehz, ty_product_type protype, ty_customer_base c, TY_KDK_JH jh, ty_repay_lsz lsz, sys_user sysuser where c.id=basic.ty_customer_id and basic.user_id=sysuser.id and c.KHNM = tkmx.khh and protype.product_code=tkmx.cpmc and tkmx.jjh=lsz.jjh and yehz.JJH = tkmx.JJH and jh.ywbh=tkmx.ywbh||'HT' group by c.zjhm ) maxqs, (select sum(lsz.df) as df , tkmx.ywbh , c.zjhm from basic_customer_information basic, TY_REPAY_TKMX tkmx , TY_REPAY_YEHZ yehz, ty_repay_lsz lsz, ty_customer_base c, ty_product_type protype, sys_user sysuser where basic.ty_customer_id =c.id and basic.user_id=sysuser.id and c.KHNM = tkmx.khh and yehz.JJH = tkmx.JJH and protype.product_code=tkmx.cpmc and lsz.jjh=tkmx.jjh and lsz.zy='批量自动扣利息'
group by tkmx.ywbh , c.zjhm ) shlx,
(select sum(lsz.df) as df , tkmx.ywbh , c.zjhm from basic_customer_information basic, TY_REPAY_TKMX tkmx , TY_REPAY_YEHZ yehz, ty_repay_lsz lsz, ty_customer_base c, ty_product_type protype, sys_user sysuser where c.id=basic.ty_customer_id and basic.user_id=sysuser.id and c.KHNM = tkmx.khh and yehz.JJH = tkmx.JJH and protype.product_code=tkmx.cpmc and lsz.jjh=tkmx.jjh
and (lsz.zy='批量自动扣本金' or lsz.zy='贷款柜台提前还款') group by tkmx.ywbh , c.zjhm) shbj, basic_customer_information basic, TY_REPAY_TKMX tkmx , TY_REPAY_YEHZ yehz, ty_product_type protype, ty_customer_base c, TY_KDK_JH jh, sys_user sysuser where c.id=basic.ty_customer_id and basic.user_id=sysuser.id and c.KHNM = tkmx.khh and protype.product_code=tkmx.cpmc and yehz.JJH = tkmx.JJH and jh.ywbh=tkmx.ywbh||'HT' and maxqs.cardid=c.zjhm and maxqs.qs=jh.qs and shlx.ywbh=tkmx.ywbh and shlx.zjhm=c.zjhm and shbj.ywbh=tkmx.ywbh and shbj.zjhm=c.zjhm and maxqs.maxjzrq <= tkmx.Dqrq
AND c.zjhm=#{certiCode}
AND c.khmc=#{customerName}
AND (select t.name from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = sysuser.id))=#{organName}
AND sysuser.display_name =#{custManagerName}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

