from customer_application_info cai left join customer_application_process aa on cai.id=aa.application_id left join basic_customer_information bai on cai.customer_id=bai.id left join sys_user su on su.id=bai.user_id left join ty_customer_base base on bai.ty_customer_id=base.id left join PRODUCT_ATTRIBUTE p on p.id=cai.product_id left join wf_process_record pr on pr.id = aa.serial_number left join wf_status_queue_record t on t.current_process = pr.id left join wf_status_info s on t.current_status = s.id
