mapper.select:
	select t.id,t.customer_id,b.ty_customer_id,b.chinese_name,t.product_id,p.product_name,b.card_id,
t.apply_quota,t.final_approval,t.status,t.CREATED_TIME,t.ACTUAL_QUOTE as reqlmt,usr.display_name, b.user_id,
(select org.name from sys_organization org where id in(select t.org_id from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = b.user_id)))as organcode
from customer_application_info t,basic_customer_information b,product_attribute p,sys_user usr
where t.customer_id=b.id  and t.product_id=p.id and usr.id = b.user_id
	and p.product_name='融耀卡'
 
 	and user_id = #{userId}
 
 
 	and APPLY_QUOTA >= #{startAmt}
 
 
 	and and APPLY_QUOTA  <=   #{endAmt}
 
 
 	and user_id = #{custManagerId}
 
 
 	and organcode = #{organName}
 
  
 	 and product_name like '%'||#{productName}||'%' 
 
 
 	 and id like '%'||#{id}||'%' 
 
 
 	and status= #{status}
 
 
	 
	 	and card_id like '%"+cardId+"%' and chinese_name like '%"+chineseName+"%' 
	 
	 
	  and card_id like '%'||#{cardId}||'%' 
	 
	  
	  and chinese_name like '%'||#{chineseName}||'%'

	select count(t.id)
	from customer_application_info t,basic_customer_information b,product_attribute p,sys_user usr
where t.customer_id=b.id  and t.product_id=p.id and usr.id = b.user_id
	and p.product_name='融耀卡'
 
 	and user_id = #{userId}
 
  
 	and APPLY_QUOTA >= #{startAmt}
 
 
 	and and APPLY_QUOTA  <=   #{endAmt}
 
 
 	and user_id = #{custManagerId}
 
 
 	and organcode = #{organName}
 
  
 	 and product_name like '%'||#{productName}||'%' 
 
 
 	 and id like '%'||#{id}||'%' 
 
 
 	and status= #{status}
 
 
	 
	 	and card_id like '%"+cardId+"%' and chinese_name like '%"+chineseName+"%' 
	 
	 
	  and card_id like '%'||#{cardId}||'%' 
	 
	  
	  and chinese_name like '%'||#{chineseName}||'%'

SELECT b.*, U.CHINESE_NAME as chineseName, U.RESIDENTIAL_ADDRESS AS residentialAddress, U.CARD_TYPE AS cardType, U.CARD_ID AS cardId, b.APPLY_QUOTA AS applyQuota, A.SERIAL_NUMBER as SERIALNUMBER, N.NODE_NAME as nodeName, A.AUDIT_TIME as auditTime, b.CREATED_TIME as createdTime, prod.PRODUCT_NAME as productName, s.display_name as delayAuditUser FROM CUSTOMER_APPLICATION_INFO b, BASIC_CUSTOMER_INFORMATION U, CUSTOMER_APPLICATION_PROCESS A, NODE_AUDIT N,NODE_AUDIT_USER p, PRODUCT_ATTRIBUTE prod, sys_user s WHERE 1=1 and s.id=u.user_id and U.ID = b.CUSTOMER_ID and a.APPLICATION_ID=b.ID AND N.ID=A.NEXT_NODE_ID and prod.id = b.PRODUCT_ID and A.NEXT_NODE_ID=p.NODE_ID and p.USER_ID=#{userId} and b.status='audit' -- and b.id not in (select APPLICATION_ID from T_APP_MANAGER_AUDIT_LOG)
and U.card_id=#{cardId}
and U.chinese_name like '%'||#{chineseName}||'%'
and N.NODE_NAME=#{nextNodeName}
order by b.CREATED_TIME

SELECT COUNT (*) FROM ( SELECT b.*, U.CHINESE_NAME as chineseName, U.RESIDENTIAL_ADDRESS AS residentialAddress, U.CARD_TYPE AS cardType, U.CARD_ID AS cardId, b.APPLY_QUOTA AS applyQuota, A.SERIAL_NUMBER as SERIALNUMBER, N.NODE_NAME as nodeName, A.AUDIT_TIME as auditTime, b.CREATED_TIME as createdTime, prod.PRODUCT_NAME as productName FROM CUSTOMER_APPLICATION_INFO b, BASIC_CUSTOMER_INFORMATION U, CUSTOMER_APPLICATION_PROCESS A, NODE_AUDIT N,NODE_AUDIT_USER p, PRODUCT_ATTRIBUTE prod WHERE 1=1 and U.ID = b.CUSTOMER_ID and a.APPLICATION_ID=b.ID AND N.ID=A.NEXT_NODE_ID and prod.id = b.PRODUCT_ID and A.NEXT_NODE_ID=p.NODE_ID and p.USER_ID=#{userId} and b.status='audit'
and U.card_id=#{cardId}
and U.chinese_name like '%'||#{chineseName}||'%'
and N.NODE_NAME=#{nextNodeName}
)

mapper.update:
	update T_APP_MANAGER_AUDIT_LOG 
	set id=#{id},AUDIT_TYPE=#{auditType},USER_ID_1=#{userId_1},USER_ID_2=#{userId_2},USER_ID_3=#{userId_3},
	EXAMINE_AMOUNT=#{examineAmount},EXAMINE_LV=#{examineLv},qx=#{qx},USER_ID_4=#{userId_4}
	where 1=1 
 
 	and APPLICATION_ID=#{applicationId}
